import React, { useState, useEffect } from "react";

export default function PlantCard({ plant }) {
  const [index, setIndex] = useState(0);

  // Auto-slide every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % plant.images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [plant.images.length]);

  return (
    <div className="border rounded-lg overflow-hidden shadow-md bg-gray-800 dark:bg-gray-900 text-gray-200 transition-all">
      {/* Image slideshow */}
      <div className="relative w-full h-48 overflow-hidden">
        <img
          src={plant.images[index]}
          alt={plant.name}
          className="w-full h-48 object-cover transition-all duration-500"
        />
        {/* Left & Right controls */}
        <button
          onClick={() =>
            setIndex((prev) =>
              prev === 0 ? plant.images.length - 1 : prev - 1
            )
          }
          className="absolute left-2 top-1/2 -translate-y-1/2 bg-black/40 text-white px-2 py-1 rounded-full hover:bg-black/60"
        >
          ‹
        </button>
        <button
          onClick={() =>
            setIndex((prev) => (prev + 1) % plant.images.length)
          }
          className="absolute right-2 top-1/2 -translate-y-1/2 bg-black/40 text-white px-2 py-1 rounded-full hover:bg-black/60"
        >
          ›
        </button>
        {/* Dots */}
        <div className="absolute bottom-2 w-full flex justify-center gap-1">
          {plant.images.map((_, i) => (
            <div
              key={i}
              className={`w-2 h-2 rounded-full ${
                i === index ? "bg-green-400" : "bg-gray-500"
              }`}
            ></div>
          ))}
        </div>
      </div>

      {/* Text details */}
      <div className="p-4">
        <h3 className="text-xl font-semibold text-green-400">{plant.name}</h3>
        <p className="text-sm text-gray-400">{plant.location}</p>
        <p className="mt-2">
          <strong>Capacity:</strong> {plant.capacity}
        </p>
        <p className="mt-2 text-gray-300">{plant.description}</p>
      </div>
    </div>
  );
}
