import React from 'react';

export default function PlantCard({ plant }) {
  return (
    <div className="border rounded-lg overflow-hidden shadow-sm bg-white dark:bg-gray-800">
      <img src={plant.image} alt={plant.name} className="w-full h-48 object-cover" />
      <div className="p-4">
        <h3 className="text-xl font-semibold text-primary-700 dark:text-primary-300">{plant.name}</h3>
        <p className="text-sm text-gray-600 dark:text-gray-300">{plant.location}</p>
        <p className="mt-2">Capacity: <span className="font-medium">{plant.capacity}</span></p>
        <p className="mt-2 text-gray-700 dark:text-gray-200">{plant.description}</p>
      </div>
    </div>
  );
}
